INSERT INTO `genel` (`genel_id`, `latince_ismi`, `turkce_isimleri`, `buyume_formu`, `anavatani`, `yetistigi_bolge`, `ailesi`, `notlar`) VALUES
(57, 'Rebutia minuscula', 'Rebutia', '\r\n KAKTÜS -', 'Arjantin', '\r\nAkdeniz Kıyı -Ege Kıyı -', 'Cactacease', '\"Red Crown Cactus, Fire Crown Cactus olarak da bilinir.\"'),
(58, 'Agave americana L.', 'Agav', '\r\n KAKTÜS -', 'Amerika', '\r\nAkdeniz Kıyı -Akdeniz Karasal -Ege Kıyı -Ege Karasal -Güneydoğu Anadolu -İç Anadolu -', 'Agavaceae', 'Bitki yaşamının sonuna doğru 5-6 metreye uzanan bir dal verir.  Bu dalın ucunda sarımsı yeşilimsi çiçekler oluşur.  Bu görüntüsü çok ilginçtir.'),
(59, 'Agave americana \'Marginata\'', 'Alacalı agav', '\r\n KAKTÜS -', 'Orta Amerika', '\r\nAkdeniz Kıyı -Akdeniz Karasal -Ege Kıyı -Ege Karasal -Güneydoğu Anadolu -İç Anadolu -', 'Agavaceae', 'Agave americana\'nın yapraklarının kenarı sarı çizgili olanıdır.'),
(60, 'Aptenia cordifolia L.', 'Buz çiçeği', '\r\n YER ÖRTÜCÜ -', 'Güney Afrika', '\r\n', 'Aizoaceae', '\"Otsu olup, çok yıllıktır Dalları sürünücü karakterdedir, yeri halı gibi kaplar. Yeşil yaprakları  etli, elips şeklindedir. Anavatanı Güney Afrika\'dır.\r\n\r\"'),
(61, 'Berberis thunbergii DC.', 'Japon kadın tuzluğu', '\r\n ÇALI -', 'Japonya', '\r\n', 'Berberidaceae', 'Berberidaceae ailesinden olan Berberis thunbergii DC rengi ve formu ile peyzajda beğenilen bir çalı halini almıştır.'),
(62, 'Buxus microphylla \'Japonica\' Rehd.', 'Japon şimşiri', '\r\n ÇALI -', 'Japonya', '\r\n', 'Buxaceae', 'Genellikle çit bitkilendirmesinde kullanılır ve budama ile verilen formal formu ile ilgi görür. Geniş ve açık alanlaarda yetiştiğinde ise oval form alır.'),
(63, 'Caesalpinia gilliesii Benth.', 'Bodur akasya', '\r\nAĞAÇCIK - ÇALI -', 'Güney Amerika', '\r\n', 'Fabaceae', 'Budama ve eğitimle küçük bir ağaçcık görüntüsü kazandırılabilir.'),
(64, 'Campsis radicans Seem.', 'Acem borusu', '\r\n ÇALI - SARILICI -', 'Amerika Birleşik Devletleri', '\r\n', 'Bignoniaceae', '10 m.\'ye kadar havai kökleri sayesinde tırmanabilen odunsu bir sarmaşıktır.'),
(65, 'Cassia artemisioides DC.', 'Çalı sinameki', '\r\n ÇALI -', 'Avusturalya', '\r\n', 'Fabaceae', 'Renkleri ve uzun süre çiçekli kalmaları ile bilinirler. Avusturalya kökenli dik bir çalıdır.'),
(66, 'Drosanthemum floribundum Haw.', 'Kedi tırnağı', '\r\n YER ÖRTÜCÜ -', 'Güney Afrika', '\r\n', 'Aizoaceae', 'Yan sürgünlerin köklendirilmesiyle çoğaltılırlar.'),
(67, 'Grevillea robusta Cunn.', 'Grevilya', '\r\nAĞAÇ -', '\"Queensland, Avusturalya\"', '\r\n', 'Proteaceae', '\"Hızlı yetişen, 18- 35m ye kadar boylanabilen bir herdemyeşil bitkidir.\"'),
(68, 'Jacaranda mimosifolia D. Don.', 'Jakaranda', '\r\nAĞAÇ -', 'Brezilya', '\r\nAkdeniz Kıyı -Ege Kıyı -', 'Bignoniaceae', 'Bitki çekici çiçekleri nedeniyle tercih edilir. 15m\'ye kadar boylanabilen düzgün bir yapıya sahiptir. Arjantin-Brazilya kökenli olmakla birlikte birçok ılıman ülkeye adapte olmuştur.'),
(80, 'Carpobrotus edulis L.', 'Kazayağı', '\r\n YER ÖRTÜCÜ - SARILICI -', 'Güney Afrika', '\r\n', 'Aizoacae', '\"Aizoaceae ailesinden Güney Afrika kökenli bir sukkulenttir. Yer örtücü,\r\nyayılıcı, sürünücü, çok yıllık çiçekli bir yayılıcıdır.\r\"'),
(81, 'Cotoneaster horizontalis Decne.', 'Dağ muşmulası', '\r\n ÇALI - YER ÖRTÜCÜ -', 'Çin', '\r\n', 'Rosaceae', '\"Yaprağını döken yada yarı herdemyeşil, 50cm\'in üzerinde 1m\'ye kadar boylanan, alçak, çok dallı, çalı formlu bir bitkidir. Yelpaze şekilli dallanması ve sonbaharda yaprakların dökülmeden aldığı renk ve dekoratif meyveleri ile ilgi çeker.\"'),
(82, 'Cotoneaster lacteus Sm.', 'Yaprak dökmeyen dağ muşmulası', '\r\n ÇALI -', 'Çin', '\r\n', 'Rosaceae', 'Dik dallanması ile ve baharda açan beyaz çiçekleri ile gösterişli bir Coneasterdir.'),
(83, 'Euonymus japonica L.', 'Taflan', '\r\n ÇALI -', '\"Çin, Japonya, Asya\"', '\r\nMarmara Kıyı -Marmara Karasal -', 'Celastraceae', '\"Topiory sanatına uygun çok iyi bir çit bitkisidir. Yaprakları bitkiyi en altına kadar sardığından, ve ağır tekstüründen dolayı renkli yapraklı ya da gösterişli çiçekleri olan bitkilere fon oluşturmada kullanılabilir.\"'),
(84, 'Fraxinus excelsior L.', 'Adi dişbudak', '\r\nAĞAÇ -', '\"Avrupa, Türkiye\"', '\r\n', 'Oleaceae', 'Gövede kabuğu gri-kahverengi tüylü ve çatlaklıdır. Arizona külü de denilmektedir.'),
(85, 'Hedera canariensis Willd.', 'Orman sarmaşığı', '\r\n YER ÖRTÜCÜ - SARILICI -', '\"Cezayir, Kanarya adaları\"', '\r\n', 'Araliaceae', ''),
(86, 'Hibiscus rosa sinensis L.', 'Japon gülü', '\r\n ÇALI -', 'Çin', '\r\n', 'Malvaceae', 'Genllikle kıyı bölgelerinde daha çok yaygınlık gösteren bir çalı türüdür.'),
(87, 'Jasminum mesnyi Hance.', 'Sarı çiçekli yasemin', '\r\n ÇALI -', 'Asya', '\r\n', 'Oleaceae', 'Herdemyeşil bu bitki baharda sarı renge bürünür.'),
(116, 'Jasminum officinale L.', 'Beyaz çiçekli yasemin', '\r\n ÇALI - SARILICI -', 'Hindistan', '\r\n', 'Oleaceae', 'Güzel kokulu bir sarılıcıdır. Baharda beyaz çiçekleri ile görsel zenginlik yaratır.'),
