INSERT INTO `yaprak` (`yaprak_id`, `yaprak_dokme`, `yaprak_tipi`, `yaprak_sekli`, `yaprak_buyuklugu`, `yaprak_kokusu`, `yaprak_dokusu`, `yaprak_rengi`, `yaprak_guz_rengi`, `yaprak_hakkinda_notlar`) VALUES
(499, 'Herdemyeşil', 'Basit', '\r\nMızraksı', 'Orta [5-10 cm]', 'Kokusuz', 'Derimsi', 'Bilgi Yok', 'Renk değiştirmez', '\"Genç bireylerde gri ve düzgün olan kabuk, yaşlanınca levhalar halinde çatlar. \r\nDeri gibi sert olan yaprakların değişik biçimleri vardır. Yaprakların alt ve üst yüzleri özellikle alt yüzleri tüylüdür. \r\"'),
(500, 'Herdemyeşil', 'Basit', '\r\nMızraksı', 'Orta [5-10 cm]', 'Kokusuz', 'Normal', 'Bilgi Yok', 'Renk değiştirmez', ''),
(501, 'Yaprak Döken', 'Konifer', '\r\nÜçgen', 'Bilgi Yok', 'Kokusuz', 'Bilgi Yok', 'Yeşil', 'KırmızıKahverengi', '\"üst yüzeyleri parlak yeşil,alt yüzleri ise açık yeşil veya açık mavi yeşil olan tüysü ve yumuşak iğne yaprakları vardır. Kışın aprağını döken ibrelilerdendir.\r\nUzun ve kısa sürgünleri vardır.Sürgün ve yaprakları karşılıklı dizilmiştir\"'),
(502, 'Herdemyeşil', 'Konifer', '\r\nÜçgen', 'Bilgi Yok', 'Kokusuz', 'Bilgi Yok', 'Bilgi Yok', 'Renk değiştirmez', '\"Çok sayıda sert ve pul görünümlü yapraklarla bezelidir. Yapraklar 3-4 cm uzunlukta ve genişlikte ikizkenar üçgen şeklindedir, keskindir ve uç noktalarında sivri kahverengi dikenle sona ererler. Yaprak kenarları sarıya çalar. Yapraklar dallarda spiral oluşturacak şekilde dağılır(sarmal dizilimlidir).\r\"'),
(503, 'Herdemyeşil', 'Konifer', '\r\nÇizgisel', 'Orta [5-10 cm]', 'Kokusuz', 'Bilgi Yok', 'Mavi Yeşil', 'Renk değiştirmez', 'İğne yaprakları 5\'lidir.'),
(504, 'Herdemyeşil', 'Konifer', '\r\nÇizgisel', 'Küçük [0-5 cm]', 'Kokusuz', 'Bilgi Yok', 'Bilgi Yok', 'KahverengiBronz', ''),
(505, 'Herdemyeşil', 'Konifer', '\r\nDiğer', 'Bilgi Yok', 'Kokusuz', 'Bilgi Yok', 'Açık Yeşil', 'Renk değiştirmez', 'Pul yapraklar birbiri üzerine sıkıca kapanmış ve sürgünleri yassıdır. Pul yaprakların arkasında stoma bandı bulunmaz. üst yüzleri parlak yeşil renklidir.'),
(506, 'Herdemyeşil', 'Konifer', '\r\nÇizgisel', 'Bilgi Yok', 'Kokulu', 'Bilgi Yok', 'Mavi Yeşil', 'Renk değiştirmez', '\"Mavi-Yeşil ya da gümüşi  renkli iğne yapraklar diğer göknarlara göre daha uzun ve sürgüne seyrek dizilmiştir, beyaz stoma çizgileri yoktur. \"\"Argentea\"\" gümüşi yapraklı, \"\"Aurea\"\" soluk-sarı iğne yapraklı kültivarları vardır.\"'),
(507, 'Herdemyeşil', 'Konifer', '\r\nÇizgisel', 'Küçük [0-5 cm]', 'Kokusuz', 'Normal', 'Yeşil', 'Renk değiştirmez', '\"İğne yapraklar çok kısa, küt, sürgünlere yatık durumda, üstte 1-2, altta 3-4 stoma çizgilidir. Ladinler içerisinde iğne yaprakları en kısa olandır.\"'),
(508, 'Herdemyeşil', '', '\r\nÇizgisel', 'Küçük [0-5 cm]', 'Kokusuz', 'Normal', 'Yeşil', 'Renk değiştirmez', '\"İğne yaprakları sivri, batıcı, sert, kalın, köşelidir. Sürgünden dik olarak çıkarlar, fırça gibi bir görünüm oluşturular.\"'),
(509, 'Herdemyeşil', 'Konifer', '\r\nDiğer', 'Bilgi Yok', 'Kokusuz', 'Bilgi Yok', 'Yeşil', 'Renk değiştirmez', 'Pul yapraklıdır'),
(510, 'Herdemyeşil', 'Konifer', '\r\nÇizgisel', 'Bilgi Yok', 'Kokusuz', 'Bilgi Yok', 'Bilgi Yok', 'Renk değiştirmez', '\"İğne yaprakları 2-3 cm. boyunda, parlak koyu yeşil, alt yüzünde iki adet beyaz stoma bandı vardır. İğne yaprağının her iki tarafında stomaların bulunuşu ile Abies nordmaniana\'dan ayırt edlilebilir.\"'),
(511, 'Herdemyeşil', 'Konifer', '\r\n', 'Bilgi Yok', 'Kokusuz', 'Bilgi Yok', 'Mavi Yeşil', 'Renk değiştirmez', ''),
(512, 'Herdemyeşil', 'Konifer', '\r\nÇizgisel', 'Bilgi Yok', 'Kokusuz', 'Bilgi Yok', 'Mavi Yeşil', 'Renk değiştirmez', '\"İğne yağrakları kısa sürgünlere 40-50 kadarı adedi bir arada rozet biçiminde dizilmiştir.1.5-2.5 cm uzunluğundaki iğne yaprakları mavi - yeşil, gümüşi - gri veya nadiren açık ya da koyu yeşil renklidir.\"'),
(513, 'Herdemyeşil', 'Konifer', '\r\nÇizgisel', 'Bilgi Yok', 'Kokusuz', 'Bilgi Yok', 'Bilgi Yok', 'Renk değiştirmez', '\"Genç sürgünleri gri renkli tüylerle kaplıdır ve iğne yapraklar sürgünden dik çıkar, tarak gibi. Yaprağın altında belirgin iki stoma çizgisi bulunur.\"'),
(514, 'Herdemyeşil', 'Konifer', '\r\nÇizgisel', 'Bilgi Yok', 'Kokusuz', 'Bilgi Yok', 'Bilgi Yok', 'Renk değiştirmez', '\"Uzun sürgün üzerindedir. Işığa yönelmiş, tarak şeklinde dizilmiştir.Yassı ve iki yüzlüdür, üst yüzü hafif oluklu, alt yüzünde 2 tane gümüşi-beyz stoma bandı bulunur.\"'),
(515, 'Herdemyeşil', 'Konifer', '\r\nDiğer', 'Bilgi Yok', 'Kokusuz', 'Bilgi Yok', 'Yeşil', 'Renk değiştirmez', '\"Pul yapraklıdır.\r\nPul yaprakların sırtlarında uzunca bir yarık ve içerisinde yağ bezeleri vardır. (sürgünler ışığa tutulduğunda belirgin olarak görülür.)\r\nsürgünler ve yapraklaroğuşturulduğunda reçine ile karışık kuvvetli maydonoz kokusuna benzer bir koku alınır.\"'),
(516, 'Herdemyeşil', 'Konifer', '\r\nDiğer', 'Bilgi Yok', 'Kokusuz', 'Bilgi Yok', 'Sarı Yeşil', 'Kahverengi', 'pul şeklinde yaprak'),
(517, 'Herdemyeşil', 'Konifer', '\r\nÇizgisel', 'Bilgi Yok', 'Kokusuz', 'Bilgi Yok', 'Mavi Yeşil', 'Renk değiştirmez', 'Yapraklar balık kılçığı görünümünde ve tüysüdür.'),
(518, 'Herdemyeşil', 'Konifer', '\r\nÇizgisel', 'Bilgi Yok', 'Kokusuz', 'Bilgi Yok', 'Bilgi Yok', 'Renk değiştirmez', ''),
(519, 'Herdemyeşil', 'Konifer', '\r\nÇizgisel', 'Bilgi Yok', 'Kokusuz', 'Bilgi Yok', 'Bilgi Yok', 'Renk değiştirmez', '\"Enine kesiti 1 mm olan ucu sivri , 15-25 mm uzunluğunda iğne yaprakları bulunmaktadır. Sürgünler üzerinde 5-7 yıl kalan iğne yaprakları dört köşeli, batıcı ve her yüzünde stoma bantları vardır.\"'),
(520, 'Herdemyeşil', 'Konifer', '\r\nÇizgisel', 'Bilgi Yok', 'Kokusuz', 'Bilgi Yok', 'Açık Yeşil', 'KırmızıBronz', '\"Kışın yaprak rengi değişir. Yeşilimsi menekşe, kırmızımsı kahve renginde görülür.\"'),
(521, 'Herdemyeşil', 'Konifer', '\r\nDiğer', 'Bilgi Yok', 'Kokusuz', 'Bilgi Yok', 'Açık Yeşil', 'Renk değiştirmez', 'Diğer türlerden farklı olarak pul yaprakların sırtında yağ bezesi bulunmaz. Yaprağın arka yüzünde çok belirgin beyaz stoma lekeleri vardır. Yaprak üst yüzü timsah derisi görünümündedir.'),
(522, 'Herdemyeşil', 'Konifer', '\r\nDiğer', 'Bilgi Yok', 'Kokusuz', 'Bilgi Yok', 'Mavi Yeşil', 'Renk değiştirmez', 'Hem pul hem iğne yapraklı.'),
(523, 'Herdemyeşil', 'Konifer', '\r\nDiğer', 'Bilgi Yok', 'Kokusuz', 'Bilgi Yok', 'Gri Yeşil', 'Renk değiştirmez', '\"Sürgünlerin uclarında yapraklar pul yaprak iken alt kısma doğru dikensi, iğne gibi batıcıdır. Botanikte bu duruma \"\"hem pul hem iğne yapraklı\"\" denir.\"'),
(524, 'Yaprak Döken', 'Konifer', '\r\nDiğer', 'Bilgi Yok', 'Kokusuz', 'Bilgi Yok', 'Açık Yeşil', 'KırmızıKahverengi', '\"Yaprakları kısa saplı, yaprakçıkları 10-17 mm uzunluğundadır. \r\nKısa sürgünlere sarmal olarak dizilir.\r\nYaprakların alt yüzlerinde soluk gri renkte stoma bantları vardır.\"'),
(525, 'Herdemyeşil', 'Konifer', '\r\nDiğer', 'Bilgi Yok', 'Kokusuz', 'Bilgi Yok', 'Bilgi Yok', 'Renk değiştirmez', 'Dökülen pul yaprakları kahverengidir. Sürgünleri önce yeşil sonra kahverengi olur. Yaprakların üst yüzlerinde iki adet stoma bandı vardı'),
(526, 'Herdemyeşil', 'Konifer', '\r\nÇizgisel', 'Bilgi Yok', 'Kokusuz', 'Bilgi Yok', 'Mavi Yeşil', 'Renk değiştirmez', ''),
(527, 'Herdemyeşil', 'Konifer', '\r\nDiğer', 'Bilgi Yok', 'Kokusuz', 'Bilgi Yok', 'Mavi Yeşil', 'Renk değiştirmez', 'Pul yaprakları kiremitimsi dizilişe sahiptir.'),
(530, 'Yaprak Döken', 'Basit', '\r\nMızraksı', 'Orta [5-10 cm]', 'Kokusuz', 'Normal', 'Yeşil', 'Sarı', '\"Yapraklar dar mızrak şeklinde uzun damla uçlu olup kenarları sık ve ince dişli, üst yüzü koyu alt yüzü gri-yeşildir.  Sap spiral, dönüktür.\"'),
(531, 'Herdemyeşil', 'Basit', '\r\nOval', 'Orta [5-10 cm]', 'Kokusuz', 'Derimsi', 'Bilgi Yok', '', '\"Genç yapraklar büyüme döneminde parlak kırmızı renklidir.\r\nYaprak kenarı kaba dişlidir.\"'),
(532, 'Yaprak Döken', 'Basit', '\r\nOval', 'Küçük [0-5 cm]', 'Kokusuz', 'Normal', 'Kırmızı', 'Kırmızı', ''),
(537, 'Yaprak Döken', 'Basit', '\r\nDiğer', 'Orta [5-10 cm]', 'Kokusuz', 'Normal', 'Bilgi Yok', 'Altın sarısı', ''),
(539, 'Herdemyeşil', 'Bileşik', '\r\nElips', 'Orta [5-10 cm]', 'Kokusuz', 'Normal', 'Yeşil', 'Kahverengi', '\"İkili tüysü yapraklar karşılıklı dizilmiştir. Çok sayıda küçük yaprakçık bulunur, yaprak kenarları tamdır.\"'),
(540, 'Yaprak Döken', 'Basit', '\r\nYuvarlak', 'Küçük [0-5 cm]', 'Kokusuz', 'Normal', 'Bilgi Yok', '', ''),
(541, 'Yaprak Döken', 'Basit', '\r\n', 'Orta [5-10 cm]', 'Kokusuz', 'Normal', 'Yeşil', 'SarıKahverengi', '\"Yumurtamsı, tabanı yuvarlak-yüreğimsi, ucu sivri, yüzeyi belirgin, onduleli ve yan damarları paraleldir. 3-10 cm boyunda, 3-5 cm genişliğindeki yapraklar parlak yeşil renklidir ve sonbaharda sararır.\r\"'),
(542, 'Yaprak Döken', 'Bileşik', '\r\nMızraksı', 'Büyük [10-15 cm]', 'Kokulu', 'Normal', 'Yeşil', 'Renk değiştirmez', '\"5-9 yaprakcıktan oluşur. Yaprakcıklar geniş-oval biçimli, ucu küt veya hafif sivrice, tam kenarlı, 6-12 cm uzunluğunda, yaprak alt yüzünde damarların birleştiği yerde tüy demetleri bulunur.\"'),
(543, 'Herdemyeşil', 'Basit', '\r\nMızraksı', 'Orta [5-10 cm]', 'Kokusuz', 'Derimsi', 'Yeşil', 'Renk değiştirmez', 'Yaprak kenarları sarı bir şerit ile çevrelenmiştir.'),
(569, 'Herdemyeşil', 'Basit', '\r\nElips', 'Büyük [10-15 cm]', 'Kokusuz', 'Derimsi', 'Bilgi Yok', 'Renk değiştirmez', ''),
(570, 'Yaprak Döken', 'Basit', '\r\nYumurta', 'Orta [5-10 cm]', 'Kokusuz', 'Derimsi', 'Bilgi Yok', 'Renk değiştirmezBronz', 'Hemen hemen sapsız olup tabana doğru daralır. Kenarları derin ve düzensiz olarak parçalanmıştır. 5-8 adet tam kenarlı loplar küt uçludur ve ender olarak tali lopçuklara ayrılır.'),
(572, 'Yaprak Döken', 'Basit', '\r\nElips', 'Orta [5-10 cm]', 'Kokusuz', 'Normal', 'Bilgi Yok', 'Kahverengi', ''),
(573, 'Yaprak Döken', 'Basit', '\r\nOval', 'Orta [5-10 cm]', 'Kokusuz', 'Normal', 'Yeşil', 'SarıKırmızı', ''),
(582, 'Yaprak Döken', 'Bileşik', '\r\n', 'Büyük [10-15 cm]', 'Kokusuz', 'Normal', 'Bilgi Yok', 'Kahverengi', ''),
(583, 'Yaprak Döken', 'Basit', '\r\nElips', 'Orta [5-10 cm]', 'Kokusuz', 'Altı Tüylü', 'Bilgi Yok', 'Renk değiştirmez', '\"yaprak kenarları çift sıralı dişli,yaprağın uç tarafı sivridir.\"'),
(584, 'Herdemyeşil', 'Basit', '\r\nÇizgisel', 'Büyük [10-15 cm]', 'Kokusuz', 'Otsu', 'Mavi Yeşil', 'Renk değiştirmez', '\"Dev bir çimen yaprağını andıran yaprakları, 1-1,5 cm enindedir ve gittikçe incelen şerit şekillidir. Çok keskin kenarlı olan bu yapraklar bitki hizasından çıkıp yere doğru kavislenir, bu durum bitkiye fıskiye görüntüsü verir.\"'),
(586, 'Yaprak Döken', 'Basit', '\r\nKaşıksı', 'Orta [5-10 cm]', 'Kokusuz', 'Üstü Tüylü', 'Yeşil', 'Sarı', '\"Yaprakların kenarları çift sıralı dişli, çoğu kez kuvvetli ve genç sürgünlerdeki yapraklarda ikinci bir ya da birkaç tali damla uçları bulunur\"'),
(587, 'Herdemyeşil', 'Basit', '\r\nElips', 'Küçük [0-5 cm]', 'Kokusuz', 'Normal', 'Bilgi Yok', 'Renk değiştirmez', '\"Karşılıklı 3-7 cm uzunluğunda koyu yeşil, parlak ve kalın yapraklara sahiptir. Yaprak kenarları 1-4 iğnemsi çıkıntılardan oluşur.\"'),
(588, 'Yaprak Döken', 'Bileşik', '\r\nÇizgisel', 'Orta [5-10 cm]', 'Kokusuz', 'Parlak', 'Yeşil', 'SarıTuruncuKahverengi', 'Yaprakları tüysüdür.'),
(589, 'Yaprak Döken', 'Basit', '\r\n', 'Orta [5-10 cm]', 'Kokusuz', 'Normal', 'Yeşil', 'KırmızıKahverengi', '\"Yapraklarındaki 4 derin lob ile ayırtedilirler.  \r\nSonbaharda kırmızı- bronz renk alırlar.\"'),
(590, 'Yaprak Döken', '', '\r\n', 'Orta [5-10 cm]', 'Kokusuz', 'Bilgi Yok', 'Yeşil', '', ''),
(591, 'Yaprak Döken', 'Basit', '\r\nMızraksı', 'Büyük [10-15 cm]', 'Kokusuz', 'Bilgi Yok', 'Gri Yeşil', '', ''),
